function update(dt)  localAnimator.clearDrawables()    --Sheath  local sheathProperties = animationConfig.animationParameter("neb-sheathProperties")  for _, sheath in ipairs(sheathProperties) do    if sheath and sheath.image ~= "" and sheath.image ~= "/assetmissing.png" then      sheath.centered = true      localAnimator.addDrawable(sheath, sheath.renderLayer)    end  end  --Charge Rings  local ringProperties = animationConfig.animationParameter("ringProperties")    if ringProperties then	localAnimator.addDrawable(ringProperties, ringProperties.renderLayer)  endend